import React, { Component } from 'react';
import SmashLab from './SmashLab.png'
import './Login.css';

class Login extends Component {

  constructor(props) {
    super(props);
    this.state = {
      pnum: "",
      enum: ""
    };
  }

  render() {

    const isValidLogin = !(this.state.pnum.trim() === "" || this.state.enum.trim() === "");
    let button;
    if (isValidLogin) {
      button = <button id="signin" className="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>;
    }
    else {
      button = "";
    }

    return (
      <div className="login text-center">
        <form className="form-signin">
          <img className="mb-4 smash_logo" src={SmashLab} alt="Smash Lab" />
          <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
          <input onChange={this.handleChange} type="text" name="pnum" id="inputPnum" className="form-control" placeholder="Participant Number" value={this.state.pnum} required autoFocus />
          <input onChange={this.handleChange} type="text" name="enum" id="inputEnum" className="form-control" placeholder="Experimenter number" value={this.state.enum} required />
          <div className="checkbox mb-3">
            <label>
                <input type="checkbox" value="consent" /> Signed Consent
            </label>
          </div>
          {button}
        </form>
      </div>
     );
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
}

export default Login;
