import React, { Component } from "react";
import Audio from '../Audio/Audio'
import "../../App.css";
import "./Questions.css";

import { insertToDatabase, formatEpoch, databaseGetAll } from "../helpers";


class Questions extends Component {

  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      edit: false
    };
  }

  componentDidMount() {
    const questions = [
      "Can I use Purell/hand sanitizer?", 
      "How long do I wash my hands for?",
      "How long do I keep the band-aid for?",
      "Unanswerable"
    ];
    this.setState({
      questions: questions
    });
  }

  handleChange = (index, event) => {
    // Update questions
    let items = [...this.state.questions];
    items[index] = event.target.value;

    this.setState({
      questions: items
    });
  }

  handleClick = (index, event) => {
    // Insert data to database
    var timeElapsed = formatEpoch(Math.floor(Date.now() / 1000) - this.props.startTime);
    var data = {timestamp: timeElapsed, action: `Question ${index}: ${this.state.questions[index]}`};
    insertToDatabase(this.props.db, data);
    // this.export();
  }

  export = () => {
    var transaction = this.props.db.transaction('log', 'readonly');
    var objectStore = transaction.objectStore('log');
    objectStore.getAll().onsuccess = function(event) {
      var result= event.target.result;
      console.log(result);
      let header = Object.keys(result[0]).join(',');
      let values = result.map(o => Object.values(o).join(',')).join('\n');
      let csv = header + '\n' + values;
      let csvContent = "data:text/csv;charset=utf-8," + csv;
      console.log(csvContent);
      var encodedUri = encodeURI(csvContent);
      window.open(encodedUri);
    };
  }

  render() {
    const isEditMode = false;


    this.done = () => {

    }

    this.repeat = () => {

    }

    return (
      <div className="container full-height mt-2">
        <h2 className="text-center">Questions</h2>
        <hr/>
        <div className="list-group">
          {this.state.questions.map((question, i) =>
            {return (isEditMode
              ? <input key={i} onChange={this.handleChange.bind(this, i)} type="text" className="form-control question mt-2" value={this.state.questions[i]} autoFocus={!i} />
              : <button key={i} onClick={this.handleClick.bind(this, i)} type="button" className="btn btn-light btn-block question">Q{i}: {question}</button>
            )}
            // <button key={i} type="button" className="list-group-item list-group-item-action question mt-2">Q{i}: {question}</button>
          )}
        </div>
        {/* <Question key={i} data_index={i+1} question={question}></Question> */}
        <div className="container text-center mt-5">
              <button type="button" onClick={this.recordTime} className="btn shadow ml-3 btn-dark btn-lg light-border">Repeat</button>
              <button type="button" onClick={this.recordTime} className="btn shadow ml-3 btn-dark btn-lg light-border">Done</button>
        </div>
        <Audio></Audio>
      </div>
    );
  }
}

export default Questions;
