import React, { Component } from "react";
import "./Procedure.css";

class Procedure extends Component {

  constructor(props) {
    super(props);
    this.state = {
      step: 0
    };
  }

  done = () => {
    this.setState({
      step: (this.state.step + 1)
    });
  }

  render() {

    const procedure = [
      "Wash hands w/ soap", 
      "Put day x supplies on clean surface outside bathroom",
      "Fresh clean towel & trash bag in bathroom",
      "Shower, remove, gently wash",
      "Dry body and wound then go to set up",
      "Pat dry w/wound gauze or qtip",
      "Cut non-stick dressing then open vaseline",
      "Using 2 qtips thickly “ice” wound with vaseline",
      "Cover with gauze and tape on",
      "See you tomorrow"
    ];

    return (
      <div className="procedure mt-5">
        <h2 className="text-white">Procedure</h2>
        <hr/>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-6">
              <div className="card mt-2">
                <div className="card-body">
                  <p id="step" className="card-text"> {procedure[this.state.step]} </p>
                </div>
              </div>
              <button type="button" onClick={this.recordTime} className="btn mt-3 btn-light btn-lg">Reply</button>
              <button type="button" onClick={this.recordTime} className="btn mt-3 btn-light btn-lg">Repeat</button>
              <button type="button" onClick={this.done} className="btn mt-3 btn-light btn-lg">Done</button>
            </div>
            <div className="col-md-6">
              <ul className="list-group mt-2 mb-5 myList">
                {procedure.map((step, i) => 
                  <li key={i} className="list-group-item">{step}</li>
                )}
              </ul>            
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Procedure;
