import React, { Component } from 'react';
import SmashLab from './SmashLab.png'
import './Login.css';
import history from '../../history';

class Login extends Component {

  constructor(props) {
    super(props);
    this.state = {
      pnum: "",
      enum: "",
      eready: false,
      pready: false,
      consent: false
    };
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });

    const data = { q: this.state.enum };

    $.getJSON({
      url: 'http://127.0.0.1:5000/search',
      data: data,
      success: function(data) {
          for (let i = 0; i < data.length; i++) {
              console.log(data);
              if (data.hasOwnProperty(event.target.value)) {
                this.setState({
                  consent: true
              })
          }
        }
      }
    });   
  }

  handleConsent = () => {
    this.setState({
      consent: !this.state.consent
    });
  }

  experimenterReady = () => {
    const data = { q: this.state.enum };

    fetch('http://127.0.0.1:5000/search', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
    })
    .catch((error) => {
      console.error('Error:', error);
    });

    this.setState ({
      eready: true
    });
  }

  participantReady = () => {
    this.setState ({
      eready: true
    });
  }


  participantReady = () => {
    history.push('/Home')
  }

  render() {

    const isValidLogin = !(this.state.pnum.trim() === "" || this.state.enum.trim() === "" || !this.state.consent);
    let expButton, partButton;
    if (isValidLogin) {
      expButton = <button id="eready" onClick={this.experimenterReady} className="btn btn-lg btn-primary btn-block">Experimenter Ready</button>;
    }
    else {
      expButton = "";
    }

    if (this.state.eready) {
      partButton = <button id="pready" onClick={this.participantReady} className="btn btn-lg btn-info btn-block" type="submit">Participant Ready</button>;
    }
    else {
      partButton = "";
    }

    return (
      <form className="login text-center">
        <div className="form-signin">
          <img className="mb-4 smash_logo" src={SmashLab} alt="Smash Lab" />
          <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
          <input onChange={this.handleChange} type="text" name="pnum" id="inputPnum" className="form-control" placeholder="Participant Number" value={this.state.pnum} required autoFocus />
          <input onChange={this.handleChange} type="text" name="enum" id="inputEnum" className="form-control" placeholder="Experimenter number" value={this.state.enum} required />
          <div className="checkbox mb-3">
            <label>
                <input onChange={this.handleConsent} type="checkbox" value="econsent" checked={this.state.eready}/> Experimenter's Consent
            </label>
          </div>
          <div className="checkbox mb-3">
            <label>
                <input onChange={this.handleConsent} type="checkbox" value="pconsent" checked={this.state.pready}/> Participant's Consent
            </label>
          </div>
          {expButton}
          {partButton}
        </div>
      </form>
     );
  }
}

export default Login;
