import React, { Component } from "react";
import "../../App.css";
import "./Procedure.css";


class Procedure extends Component {

  constructor(props) {
    super(props);
    this.state = {
      step: 0,
      stop: 0
    };
    this.startTime = Date.now();
  }

  recordTime = () => {
    var elapsedTime = Date.now() - this.startTime;
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      console.log('getUserMedia supported.');
      navigator.mediaDevices.getUserMedia (
         // constraints - only audio needed for this app
         {
            audio: true
         })

         // Success callback
         .then(function(stream) {


         })

         // Error callback
         .catch(function(err) {
            console.log('The following getUserMedia error occured: ' + err);
         }
      );
    } else {
      console.log('getUserMedia not supported on your browser!');
    }
    mediaRecorder.start();
    console.log(mediaRecorder.state);
    console.log("recorder started");
    let chunks = [];

    mediaRecorder.ondataavailable = function(e) {
      chunks.push(e.data);
    }
  }

  stop = () => {
    stop.onclick = function() {
      mediaRecorder.stop();
      console.log(mediaRecorder.state);
      console.log("recorder stopped");
      }
      mediaRecorder.onstop = function(e) {
        console.log("recorder stopped");

        const clipName = prompt('Enter a name for your sound clip');

        const clipContainer = document.createElement('article');
        const clipLabel = document.createElement('p');
        const audio = document.createElement('audio');
        const deleteButton = document.createElement('button');

        clipContainer.classList.add('clip');
        audio.setAttribute('controls', '');
        deleteButton.innerHTML = "Delete";
        clipLabel.innerHTML = clipName;

        clipContainer.appendChild(audio);
        clipContainer.appendChild(clipLabel);
        clipContainer.appendChild(deleteButton);
        soundClips.appendChild(clipContainer);

        const blob = new Blob(chunks, { 'type' : 'audio/ogg; codecs=opus' });
        chunks = [];
        const audioURL = window.URL.createObjectURL(blob);
        audio.src = audioURL;

        deleteButton.onclick = function(e) {
          let evtTgt = e.target;
          evtTgt.parentNode.parentNode.removeChild(evtTgt.parentNode);
        }
      }
  }

  done = () => {
    this.setState({
      step: (this.state.step + 1) 
    });
    this.recordTime();
  }

  back = () => {
    this.setState({
      step: (this.state.step - 1)
    });
  }

  render() {

    const procedure = [
      "Wash hands w/ soap", 
      "Put day x supplies on clean surface outside bathroom",
      "Fresh clean towel & trash bag in bathroom",
      "Shower, remove, gently wash",
      "Dry body and wound then go to set up",
      "Pat dry w/wound gauze or qtip",
      "Cut non-stick dressing then open vaseline",
      "Using 2 qtips thickly “ice” wound with vaseline",
      "Cover with gauze and tape on",
      "See you tomorrow"
    ];

    return (
      <div className="full-height mt-2">
        <h2 className="text-white text-center">Procedure</h2>
        <hr/>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-6">
              <div className="card mt-2">
                <div className="card-body text-center">
                  <h5 className="card-title">Step:</h5>
                  <hr/>
                  <p id="step" className="card-text"> {procedure[this.state.step]} </p>
                </div>
              </div>
              <div className="text-center">
                <button type="button" onClick={this.recordTime} className="shadow btn mt-3 ml-3 btn-light btn-lg dark-border">Repeat</button>
                <button type="button" onClick={this.done} className="shadow btn mt-3 ml-3 btn-light btn-lg dark-border">Done</button>
                <button type="button" onClick={this.stop} className="shadow btn mt-3 ml-3 btn-light btn-lg dark-border">Doctor</button>
              </div>
            </div>
            <div className="col-md-6">
              <ul className="list-group mt-2 mb-5 procedure-list">
                {procedure.map((step, i) => 
                  <li key={i} id={'l' + i} className={`list-group-item ${(this.state.step === i) ? 'active' : ''}`}>{step}</li>
                )}
              </ul>            
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Procedure;
