import React, { Component } from "react";
import "../../App.css";
import "./Procedure.css";
import { insertToDatabase, formatEpoch } from "../helpers";


class Procedure extends Component {

  constructor(props) {
    super(props);
    this.state = {
      procedure: [],
      step: 0
    };
  }

  componentDidMount() {
    // Fetch procedure after mount
    // this.getProcedure();
  }

  getProcedure = () => {
    // Get a list of procedures
    fetch('/api/procedure')
    .then(response => response.json())
    .then(data => {
      this.procedure = data;
    })
  }

  done = () => {
    // Update step
    this.setState({
      step: (this.state.step + 1),
      edit: false
    });

    // Insert data to database
    var timeElapsed = formatEpoch(Math.floor(Date.now() / 1000) - this.props.startTime);
    var data = {timestamp: timeElapsed, action: `Procedure (Step ${this.state.step}): ${this.procedure[this.state.step]}`};
    insertToDatabase(this.props.db, data);
  }

  back = () => {
    this.setState({
      step: (this.state.step - 1)
    });
  }

  render() {

    const isEditMode = true;

    this.procedure = [
      "Wash hands w/ soap", 
      "Put day x supplies on clean surface outside bathroom",
      "Fresh clean towel & trash bag in bathroom",
      "Shower, remove, gently wash",
      "Dry body and wound then go to set up",
      "Pat dry w/wound gauze or qtip",
      "Cut non-stick dressing then open vaseline",
      "Using 2 qtips thickly “ice” wound with vaseline",
      "Cover with gauze and tape on",
      "See you tomorrow"
    ];

    return (
      <div className="full-height mt-2">
        <h2 className="text-white text-center">Procedure</h2>
        <hr/>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-6">
              <div className="card mt-2">
                <div className="card-body text-center">
                  <h5 className="card-title">Step {this.state.step}:</h5>
                  <hr/>
                  <p id="step" className="card-text"> {this.procedure[this.state.step]} </p>
                </div>
              </div>
              <div className="text-center mt-5">
                <button type="button" onClick={this.repeaat} className="shadow btn ml-3 btn-light btn-lg dark-border">Repeat</button>
                <button type="button" onClick={this.done} className="shadow btn ml-3 btn-light btn-lg dark-border">Done</button>
                <button type="button" onClick={this.doctor} className="shadow btn ml-3 btn-danger btn-lg dark-border">Doctor</button>
                <section className="sound-clips">
                </section>
              </div>
            </div>
            <div className="col-md-6">
              <ul className="list-group mt-2 mb-5 procedure-list">
                {this.procedure.map((step, i) => 
                  {return (isEditMode
                    ? <input key={i} onChange={this.handleChange} type="text" className="form-control question" defaultValue={step} value={this.state.procedure[i]} autoFocus={!i} />
                    : <li key={i} id={'l' + i} className={`list-group-item ${(this.state.step === i) ? 'active' : ''}`}>{step}</li>
                  )}

                )}
              </ul>            
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Procedure;
