import React, { Component } from "react";
import "../../App.css";
import "./Procedure.css";


class Procedure extends Component {

  constructor(props) {
    super(props);
    this.state = {
      db: null,
      step: 0
    };
  }

  componentDidMount() {
    // Open IndexedDB database
    var request = window.indexedDB.open("mohs", 3);

    request.onsuccess = function(event) {
      this.setState({
        db: event.target.result
      });
    }

    request.onerror = function(event) {
      // Generic error handler for all errors targeted at this database's
      // requests!
      console.error("Database error: " + event.target.errorCode);
    };

    request.onupgradeneeded = function(event) {
      let db = event.target.result;

      // Create an objectStore to create a log file
      var objectStore = db.createObjectStore("log", { autoIncrement : true });
    };
  }

  done = () => {
    this.setState({
      step: (this.state.step + 1) 
    });

    Math.floor(Date.now() / 1000)

    var data = {timestamp: 'my_time', action: 'done'};

    console.log(this.state.db)

    var transaction = this.state.db.transaction(["mohs"], "readwrite");

    transaction.oncomplete = function(event) {
      console.log("All done!");
    };

    transaction.onerror = function(event) {

    };

    var objectStore = transaction.objectStore("mohs");
    var request = objectStore.add();
    request.onsuccess = function(event) {

    }
  }

  back = () => {
    this.setState({
      step: (this.state.step - 1)
    });
  }

  render() {

    const procedure = [
      "Wash hands w/ soap", 
      "Put day x supplies on clean surface outside bathroom",
      "Fresh clean towel & trash bag in bathroom",
      "Shower, remove, gently wash",
      "Dry body and wound then go to set up",
      "Pat dry w/wound gauze or qtip",
      "Cut non-stick dressing then open vaseline",
      "Using 2 qtips thickly “ice” wound with vaseline",
      "Cover with gauze and tape on",
      "See you tomorrow"
    ];

    return (
      <div className="full-height mt-2">
        <h2 className="text-white text-center">Procedure</h2>
        <hr/>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-6">
              <div className="card mt-2">
                <div className="card-body text-center">
                  <h5 className="card-title">Step:</h5>
                  <hr/>
                  <p id="step" className="card-text"> {procedure[this.state.step]} </p>
                </div>
              </div>
              <div className="text-center mt-5">
                <button type="button" onClick={this.repeaat} className="shadow btn ml-3 btn-light btn-lg dark-border">Repeat</button>
                <button type="button" onClick={this.done} className="shadow btn ml-3 btn-light btn-lg dark-border">Done</button>
                <button type="button" onClick={this.doctor} className="shadow btn ml-3 btn-danger btn-lg dark-border">Doctor</button>
                <section className="sound-clips">
                </section>
              </div>
            </div>
            <div className="col-md-6">
              <ul className="list-group mt-2 mb-5 procedure-list">
                {procedure.map((step, i) => 
                  <li key={i} id={'l' + i} className={`list-group-item ${(this.state.step === i) ? 'active' : ''}`}>{step}</li>
                )}
              </ul>            
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Procedure;
