import React, { Component } from "react";
import Audio from '../Audio/Audio'

import "../../App.css";
import "./Questions.css";


class Questions extends Component {

  constructor(props) {
    super(props);
    this.state = {
      question: ""
    };
  }

  handleChange = (event) => {
    this.setState({
      question: event.target.value
    });
  }

  render() {
    const isEditMode = true;

    const questions = [
      "Can I use Purell/hand sanitizer?", 
      "How long do I wash my hands for?",
      "How long do I keep the band-aid for?",
      "Unanswerable"
    ];

    this.done = () => {

    }

    this.repeat = () => {

    }

    return (
      <div className="container full-height mt-2">
        <h2 className="text-center">Questions</h2>
        <hr/>
        <div className="list-group">
          {questions.map((question, i) =>
            {return (isEditMode
              ? <input key={i} onChange={this.handleChange} type="text" className="form-control question mt-2" value={this.state.question} defaultValue={`Q${i}: ${question}`} />
              : <button key={i} type="button" className="btn btn-light btn-block question">Q{i}: {question}</button>
            )}
            // <button key={i} type="button" className="list-group-item list-group-item-action question mt-2">Q{i}: {question}</button>
          )}
        </div>
        {/* <Question key={i} data_index={i+1} question={question}></Question> */}
        <div className="container text-center mt-5">
              <button type="button" onClick={this.recordTime} className="btn shadow ml-3 btn-dark btn-lg light-border">Repeat</button>
              <button type="button" onClick={this.recordTime} className="btn shadow ml-3 btn-dark btn-lg light-border">Done</button>
        </div>
        <Audio></Audio>
      </div>
    );
  }
}

export default Questions;
