import React, { Component } from "react";
import "../../App.css";
import "./Procedure.css";


class Procedure extends Component {

  constructor(props) {
    super(props);
    this.state = {
      step: 0
    };
    this.mediaRecorder = {};
    this.chunks = [];
  }

  componentDidMount() {

  }

  startRecording = (stream) => {
    // Start recording
    this.mediaRecorder = new MediaRecorder(stream);
    this.mediaRecorder.start();

    // Start visualizing audio
    this.visualize(stream);

    console.log(this.mediaRecorder.state);
    console.log("recorder started");

    var dat = this;
    this.mediaRecorder.ondataavailable = function(e) {
      dat.chunks.push(e.data);
    }
  }

  recordTime = () => {
    // Ensure getUserMedia is supported
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      console.log('getUserMedia supported.');
      navigator.mediaDevices.getUserMedia (
         // constraints - only audio needed for this app
         {
            audio: true
         })

         // Success callback, start recording
         .then((stream) => this.startRecording(stream))

         // Error callback
         .catch(function(err) {
            console.log('The following getUserMedia error occured: ' + err);
         }
      );
    } else {
      console.log('getUserMedia not supported on your browser!');
    }
  }

  visualize = (stream) => {
    const canvas = document.querySelector('.visualizer');
    const canvasCtx = canvas.getContext("2d");
    let audioCtx;

    if(!audioCtx) {
      audioCtx = new AudioContext();
    }

    const source = audioCtx.createMediaStreamSource(stream);

    const analyser = audioCtx.createAnalyser();
    analyser.fftSize = 2048;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    source.connect(analyser);
    //analyser.connect(audioCtx.destination);

    draw()

    function draw() {
      const WIDTH = canvas.width
      const HEIGHT = canvas.height;

      requestAnimationFrame(draw);

      analyser.getByteTimeDomainData(dataArray);

      canvasCtx.fillStyle = 'rgb(200, 200, 200)';
      canvasCtx.fillRect(0, 0, WIDTH, HEIGHT);

      canvasCtx.lineWidth = 2;
      canvasCtx.strokeStyle = 'rgb(0, 0, 0)';

      canvasCtx.beginPath();

      let sliceWidth = WIDTH * 1.0 / bufferLength;
      let x = 0;


      for(let i = 0; i < bufferLength; i++) {

        let v = dataArray[i] / 128.0;
        let y = v * HEIGHT/2;

        if(i === 0) {
          canvasCtx.moveTo(x, y);
        } else {
          canvasCtx.lineTo(x, y);
        }

        x += sliceWidth;
      }

      canvasCtx.lineTo(canvas.width, canvas.height/2);
      canvasCtx.stroke();

    }
  }

  done = () => {
    this.setState({
      step: (this.state.step + 1) 
    });
    this.recordTime();
  }

  back = () => {
    this.setState({
      step: (this.state.step - 1)
    });
  }

  render() {

    const procedure = [
      "Wash hands w/ soap", 
      "Put day x supplies on clean surface outside bathroom",
      "Fresh clean towel & trash bag in bathroom",
      "Shower, remove, gently wash",
      "Dry body and wound then go to set up",
      "Pat dry w/wound gauze or qtip",
      "Cut non-stick dressing then open vaseline",
      "Using 2 qtips thickly “ice” wound with vaseline",
      "Cover with gauze and tape on",
      "See you tomorrow"
    ];

    return (
      <div className="full-height mt-2">
        <h2 className="text-white text-center">Procedure</h2>
        <hr/>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-6">
              <div className="card mt-2">
                <div className="card-body text-center">
                  <h5 className="card-title">Step:</h5>
                  <hr/>
                  <p id="step" className="card-text"> {procedure[this.state.step]} </p>
                </div>
              </div>
              <div className="text-center">
                <button type="button" onClick={this.recordTime} className="shadow btn mt-3 ml-3 btn-light btn-lg dark-border">Record</button>
                <button type="button" onClick={this.done} className="shadow btn mt-3 ml-3 btn-light btn-lg dark-border">Done</button>
                <button type="button" onClick={this.stop} className="shadow btn mt-3 ml-3 btn-light btn-lg dark-border">Stop</button>
                <section className="sound-clips">
                </section>
              </div>
            </div>
            <div className="col-md-6">
              <ul className="list-group mt-2 mb-5 procedure-list">
                {procedure.map((step, i) => 
                  <li key={i} id={'l' + i} className={`list-group-item ${(this.state.step === i) ? 'active' : ''}`}>{step}</li>
                )}
              </ul>            
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Procedure;
